import React, { Component } from 'react';
import Calender from './Calender';
import getDateProps from '../../utils/date'; 

class Dashboard extends Component {
    constructor(){
        super()
        this.renderNextMonth = this.renderNextMonth.bind(this);
        this.renderPreviousMonth = this.renderPreviousMonth.bind(this);
    }  

	renderPreviousMonth() {
        var { year, month, todaysDate } = this.props.dates;
        if(month === 1) {
            year -= 1
            month = 12;
        } else {
            month -= 1
        }
        var date = { year, month, todaysDate }
        this.props.setPrevious(date) 
    }

    renderNextMonth() {
        var { year, month, todaysDate } = this.props.dates;
        if(month === 12) {
            year += 1
            month = 1;
        } else {
            month += 1
        }
        var date = { year, month, todaysDate }
        this.props.setNext(date) 
    }

    render() {
    let { start, end, year, month } = getDateProps() 
    let months = ["January", "February", "March", "April", "May", 
                  "June", "July", "August", "September", "October", 
                  "November", "December"]

    const calender = 
        <div id="dashboard" className="container fluid" >
			<h1>{months[month - 1]} {year}</h1>
			<Calender 
			start={start} 
			end={end} 
			year={year} 
			month={month}/>
          <button type="button" className="btn btn-primary btn-sm" onClick={this.renderPreviousMonth}>Previous</button>
          <button type="button" className="btn btn-primary btn-sm" onClick={this.renderNextMonth}>Next</button>
		</div>

    return ( this.state.mobileView ? mobile : desktop )
  };
}

export default Dashboard;
