import React, { Component } from 'react';

class DateObj extends Component {
    static defaultProps = {
      day: "",
      date: null,
      dayEvents: [],
      dayToday: false, // True passed in if it IS today's date
    }



  render(){

    var complete = false; //All habits have been completed, and the day should be marked as such
    var habitComplete = [];

    for(let i = 0; i < this.props.dayEvents.length; i++) {
      // If dates array exists...
      if(this.props.dayEvents[i].dates) {
        // ...iterate through this array... 
        for(let j = 0; j < this.props.dayEvents[i].dates.length; j++ ) {
        // ...and if there is a date therein which matches the current day...
        if( this.props.date.toISOString().split("T")[0] === this.props.dayEvents[i].dates[j].date ) {
            habitComplete.push(true) //...push to tally of dayevents complete
          }
        }
      }
    }
    if (habitComplete.length === this.props.dayEvents.length) { //if all habits complete, day event is complete
      complete = true;
    }

    var styleDT = {
      backgroundColor:"black",
      color:"white"
    }
    var styleComplete = {
      backgroundColor:"green"
    }
    var styleNotComplete = {
      backgroundColor:"#808080"
    }

    if(this.props.dayToday) { //Highlight if TD, activities complete ot not 
      return(
        <td style={styleDT}>{this.props.day}</td>
      )
    } else if (this.props.dayEvents.length > 0 && complete ) {
      return(
        <td style={styleComplete}>{this.props.day}</td>
      )
    } else if(this.props.dayEvents.length > 0 && !complete) {
      return(
        <td style={styleNotComplete}>{this.props.day}</td>
      )
    } else { 
      return(
        <td>{this.props.day}</td>
      );
    };

  };

};


export default DateObj;
