import React, { Component } from 'react';

class DateObj extends Component {

	constructor() {
		super()
		this.state = {
			clicked: false
		}
		this.dateClicked = this.dateClicked.bind(this)
	}

    static defaultProps = {
		day: "",
		date: null,
		dayToday: false, // True passed in if it IS today's date
		booked: false
    }


	dateClicked(e) {
		if(!booked) {
			this.setState({
				clicked: !this.state.clicked 
			})
		}
	}

  render(){

	var styleDT = {
		backgroundColor:"black",
		color:"white"
	}
	var styleClicked = {
		backgroundColor: "green",
		color: "black"
	}
	var styleUnavailable = {
		backGroundcolor: "grey",
		color: "black"
	}

    if(this.props.dayToday) { //Highlight if TD, activities complete ot not 
      return(
        <td onClick={this.dateClicked} style={this.state.clicked ? styleClicked : styleDT}>{this.props.day}</td>
      )
    } else { 
      return(
        <td style={this.state.clicked ? styleClicked : null} onClick={this.dateClicked}>{this.props.day}</td>
      )
    }
  }
}


export default DateObj;
