import React, { Component } from 'react';

class DateObj extends Component {
    static defaultProps = {
      day: "",
      date: null,
      dayEvents: [],
      dayToday: false, // True passed in if it IS today's date
    }



  render(){

	var styleDT = {
      backgroundColor:"black",
      color:"white"
    }
    var styleComplete = {
      backgroundColor:"green"
    }
    var styleNotComplete = {
      backgroundColor:"#808080"
    }

    if(this.props.dayToday) { //Highlight if TD, activities complete ot not 
      return(
        <td style={styleDT}>{this.props.day}</td>
      )
    } else if (this.props.dayEvents.length > 0 && complete ) {
      return(
        <td style={styleComplete}>{this.props.day}</td>
      )
    } else if(this.props.dayEvents.length > 0 && !complete) {
      return(
        <td style={styleNotComplete}>{this.props.day}</td>
      )
    } else { 
      return(
        <td>{this.props.day}</td>
      );
    };

  };

};


export default DateObj;
